class Message{constructor(){const containerId='message-container';this.containerEl=document.getElementById(containerId);if(!this.containerEl){this.containerEl=document.createElement('div');this.containerEl.id=containerId;document.body.appendChild(this.containerEl)}}show({type='info',text='',duration=2000,closeable=false}){let messageEl=document.createElement('div');messageEl.className='message move-in';messageEl.innerHTML=`<span class="icon icon-${type}"></span><div class="text">${text}</div>`;if(closeable){let closeEl=document.createElement('div');closeEl.className='close icon icon-close';messageEl.appendChild(closeEl);closeEl.addEventListener('click',()=>{this.close(messageEl)})}this.containerEl.appendChild(messageEl);if(duration>0){setTimeout(()=>{this.close(messageEl)},duration)}}close(messageEl){messageEl.className=messageEl.className.replace('move-in','');messageEl.className+='move-out';messageEl.addEventListener('animationend',()=>{messageEl.setAttribute('style','height: 0; margin: 0')});messageEl.addEventListener('transitionend',()=>{messageEl.remove()})}}
